/**
 * @fileoverview Connector Save Export.
 */
import { ok } from "./a2/utils";
export { invoke as default, describe };
function createPackageJson() {
    return [
        {
            parts: [
                {
                    json: {
                        name: "new-project",
                        version: "1.0.0",
                        main: "index.js",
                        scripts: {
                            start: "http-server .",
                        },
                        keywords: [],
                        author: "",
                        license: "ISC",
                        description: "",
                        dependencies: {
                            "http-server": "^14.1.1",
                        },
                    },
                },
            ],
        },
    ];
}
async function writeFile(caps, dir, name, data) {
    const path = `/mnt/fs/${dir}/${name}`;
    return caps.write({ path, data });
}
async function invoke({ id, context }, caps) {
    if (!context) {
        console.warn("No data to save");
        return { context: [] };
    }
    const writingIndex = await writeFile(caps, id, "index.html", context);
    if (!ok(writingIndex))
        return writingIndex;
    const writingPackage = await writeFile(caps, id, "package.json", createPackageJson());
    if (!ok(writingPackage))
        return writingPackage;
    return { context };
}
async function describe() {
    return {
        title: "Save To Local File System",
        metadata: {
            tags: ["connector-save"],
        },
        inputSchema: {
            type: "object",
            properties: {
                context: {
                    type: "array",
                    items: { type: "object", behavior: ["llm-content"] },
                    title: "Context in",
                },
            },
        },
        outputSchema: {
            type: "object",
            properties: {
                context: {
                    type: "array",
                    items: { type: "object", behavior: ["llm-content"] },
                    title: "Context out",
                },
            },
        },
    };
}
