/**
 * @fileoverview Add a description for your module here.
 */

import { err, ok } from "./a2/utils";
import read from "@read";

export { invoke as default, describe };

type InitializeInput = {
  stage: "initialize";
  id: string;
};

type InitializeOutput = {
  title: string;
  configuration: object;
};

type ReadInput = {
  stage: "read";
  id: string;
  configuration: object;
};

type ReadOutput = {
  schema: Schema;
  values: {
    data: LLMContent[];
  };
};

type WriteInput = {
  stage: "write";
};

type WriteOutput = {};

type Inputs = {
  context?: { parts?: { json?: InitializeInput | ReadInput | WriteInput }[] }[];
};

type Outputs = {
  context: { parts: { json: InitializeOutput | ReadOutput | WriteOutput }[] }[];
};

function cx(json: InitializeOutput | ReadOutput | WriteOutput): Outputs {
  return { context: [{ parts: [{ json }] }] };
}

async function invoke({ context }: Inputs): Promise<Outcome<Outputs>> {
  const inputs = context?.at(-1)?.parts?.at(0)?.json;
  if (!inputs) return err(`Can't configure Folio: invalid input structure`);

  if (inputs.stage === "initialize") {
    return cx({ title: "Untitled Folio", configuration: {} });
  } else if (inputs.stage === "read") {
    const reading = await read({ path: `/local/folio/${inputs.id}` });
    const data = ok(reading) ? reading.data : [];
    return cx({
      schema: {
        type: "object",
        properties: {
          data: {
            type: "array",
            items: { type: "object", behavior: ["llm-content"] },
          },
        },
      },
      values: { data },
    });
  }
  return err(`Not implemented`);
}

async function describe() {
  return {
    title: "Configure a Folio",
    description:
      "Helps configure a new folio or edit configuration of an existing folio",
    metadata: {
      tags: ["connector-configure"],
    },
    inputSchema: {
      type: "object",
    } satisfies Schema,
    outputSchema: {
      type: "object",
      properties: {
        context: {
          type: "array",
          items: { type: "object", behavior: ["llm-content"] },
          title: "Context out",
        },
      },
    } satisfies Schema,
  };
}
