    }
    async execute(execute, mutableContext, objective, makeList) {
        const planning = await plannerPrompt(mutableContext, objective, this.extraPlannerPrompt, true).invoke();
        if (!ok(planning))
            return planning;
        let plan = getPlan(planning.last);
        if (!ok(plan))
            return plan;
        const results = [];
        let max = plan.todo.length + OVERRUN_BUFFER;
        let organizeResults = false;
        let planDescription = "Here is my starting plan";
        while (--max) {
            const task = plan.todo.at(0);
            if (plan.summarizeResults) {
                organizeResults = true;
            }
            if (!task)
                break;
            await report({
                actor: "Planner",
                category: "Progress update",
                name: "Thinking",
                icon: "laps",
                details: `

Here's my thinking:

${plan.thinking}

${planDescription}:

${plan.todo.map((item) => `1. ${item.label}`).join("\n")}

I will now go over the plan in order, thinking after each step
and adjusting the plan if necessary.`,
            });
            const result = await execute(task);
            if (result) {
                mutableContext.push(toLLMContent(task.task), result);
                results.push(result);
            }
            this.tasks.push(task.task);
            const thinking = await thinkingPlannerPrompt(mutableContext, objective, plan, this.tasks, this.extraPlannerPrompt).invoke();
            if (!ok(thinking))
                return thinking;
            const newPlan = getPlan(thinking.last);
            if (!ok(newPlan))
                return newPlan;
            plan = newPlan;
            planDescription = "Here are the remaining steps in the plan";
        }
        if (organizeResults) {
            await report({
                actor: "Planner",
                category: "Organizing work into a report",
                name: "Organizing work report",
                icon: "laps",
                details: `I will now organize all of my work into a report.`,
            });
            const organizing = await organizerPrompt(results, objective, makeList).invoke();
            if (!ok(organizing))
                return organizing;
