/**
 * @fileoverview Turn anything into a list, make a plan for that list, and go over it
 */
import { Template } from "./a2/template";
import { ToolManager } from "./a2/tool-manager";
import { GeminiPrompt } from "./a2/gemini-prompt";
import { type GeminiSchema, type Tool } from "./a2/gemini";
import { type Params } from "./a2/common";
import { ok, err, toLLMContent, llm } from "./a2/utils";

export { invoke as default, describe };

type Inputs = {
  context: LLMContent[];
  plan: LLMContent;
} & Params;

type Outputs = {
  context: LLMContent[];
};

type Plan = {
  todo: {
    task: string;
  }[];
};

function planSchema(): GeminiSchema {
  return {
    type: "object",
    properties: {
      todo: {
        type: "array",
        items: {
          type: "object",
          properties: { task: { type: "string" } },
        },
      },
    },
  };
}

function plannerPrompt(plan: LLMContent): GeminiPrompt {
  const contents = [plan];
  const systemInstruction = llm`
You are a planner. 
You are to create a precise plan for a given objective. This plan will be executed by others 
and your responsibility is to produce a plan that fulfills the objective.

Your output must be a valid JSON of the following format:

\`\`\`json
{
  "todo": [{
    "task": "string, The task description. Use action-oriented language, starting with a verb that fits the task."
  }]
}
\`\`\`
`.asContent();
  return new GeminiPrompt({
    body: {
      contents,
      systemInstruction,
      generationConfig: {
        responseSchema: planSchema(),
        responseMimeType: "application/json",
      },
    },
  });
}

async function executePlan(
  plan: Plan,
  toolManager: ToolManager
): Promise<Outcome<LLMContent[]>> {
  const errors: string[] = [];
  const results = await Promise.all(
    plan.todo.map(async (item) => {
      const executing = await new GeminiPrompt(
        {
          body: {
            contents: [toLLMContent(item.task)],
            tools: toolManager.list(),
          },
        },
        toolManager
      ).invoke();
      if (!ok(executing)) {
        errors.push(executing.$error);
        return;
      }
      return executing.last;
    })
  );
  if (errors.length > 0) {
    return err(errors.join("\n\n"));
  }
  return results as LLMContent[];
}

async function invoke({
  context,
  plan,
  ...params
}: Inputs): Promise<Outcome<Outputs>> {
  const toolManager = new ToolManager();
  const template = new Template(plan);
  const substituting = await template.substitute(
    params,
    async ({ path: url }) => toolManager.addTool(url)
  );
  if (!ok(substituting)) return substituting;

  const planning = await plannerPrompt(plan).invoke();
  if (!ok(planning)) return planning;

  const planPart = planning.last.parts.at(0);
  if (!planPart || !("json" in planPart)) {
    // TODO: Error recovery.
    return err(`Gemini generated invalid plan`);
  }
  const iterating = await executePlan(planPart.json as Plan, toolManager);
  if (!ok(iterating)) return iterating;
  const oneContent = {
    role: "model",
    parts: iterating.flatMap((item) => {
      return item.parts;
    }),
  };
  return { context: [oneContent] };
}

type DescribeInputs = {
  inputs: {
    plan: LLMContent;
  };
};

async function describe({ inputs: { plan } }: DescribeInputs) {
  const template = new Template(plan);
  return {
    inputSchema: {
      type: "object",
      properties: {
        context: {
          type: "array",
          items: { type: "object", behavior: ["llm-content"] },
          title: "Context in",
          behavior: ["main-port"],
        },
        plan: {
          type: "object",
          behavior: ["llm-content", "config", "hint-preview"],
          title: "Instruction",
          description:
            "Describe what will be turned into a list and then gone over",
        },
        ...template.schemas(),
      },
    } satisfies Schema,
    outputSchema: {
      type: "object",
      properties: {
        context: {
          type: "array",
          items: { type: "object", behavior: ["llm-content"] },
          title: "Context out",
        },
      },
      behavior: ["at-wireable"],
      ...template.requireds(),
      additionalProperties: false,
    } satisfies Schema,
    title: "Go over a list",
    description:
      "Turn anything into a list, make a plan for that list, and go over it",
    metadata: {
      icon: "laps",
      tags: ["quick-access", "generative", "experimental"],
      order: 102,
    },
  };
}
