import fetch from "@fetch";
import read from "@read";
import write from "@write";
import query from "@query";
import { ok, err } from "./a2/utils";
export { McpClient };
function json(result) {
    if (!ok(result))
        return result;
    const j = result.data?.at(-1)?.parts.at(0)?.json;
    if (!j) {
        return err(`Invalid result structure`);
    }
    return j;
}
function sse(result) {
    const e = json(result);
    if (!ok(e))
        return e;
    return e.data;
}
function rpc(result, status) {
    const e = sse(result);
    if (!ok(e))
        return e;
    return e.result;
}
class McpClient {
    connectorId;
    url;
    #id = 0;
    #messageEndpoint = null;
    constructor(connectorId, url) {
        this.connectorId = connectorId;
        this.url = url;
    }
    #path() {
        return `/session/mcp/${this.connectorId}/stream`;
    }
    #newId() {
        return ++this.#id;
    }
    async call(body) {
        const file = this.#path();
        const url = this.url;
        const id = this.#newId();
