        const calling = await fetch({
            url,
            file,
            stream: "sse",
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json,text/event-stream",
            },
            body: JSON.stringify(body),
        });
        if (!ok(calling))
            return calling;
        const path = calling.response;
        const response = rpc(await read({ path }), calling.status);
        return response;
    }
    async connect() {
        const file = this.#path();
        const url = this.url;
        const id = this.#newId();
        // send initialize request
        const initializing = await this.call({
            jsonrpc: "2.0",
            id,
            method: "initialize",
            params: {
                protocolVersion: "2024-11-05",
                clientInfo: {
                    name: "Breadboard",
                    version: "1.0.0",
                },
                capabilities: {
                    tools: {},
                },
            },
        });
        if (!ok(initializing))
            return initializing;
        const confirmInitialization = await this.call({
            jsonrpc: "2.0",
            method: "notifications/initialized",
        });
        return initializing;
    }
    async listTools() {
        const url = this.url;
        const id = this.#newId();
        const file = this.#path();
        // get list of tools
        const askToListTools = await this.call({
            jsonrpc: "2.0",
            id,
            method: "tools/list",
        });
        if (!ok(askToListTools))
            return askToListTools;
        return askToListTools.tools;
    }
    async callTool(name, args) {
        const url = this.url;
        const id = this.#newId();
        const file = this.#path();
        // Call tool
        const askToCallTool = await this.call({
            jsonrpc: "2.0",
            id,
            method: "tools/call",
            params: { name, arguments: args },
        });
        if (!ok(askToCallTool))
            return askToCallTool;
        return askToCallTool.content;
    }
    async disconnect() {
        const path = this.#path();
        const deleting = await write({ path, delete: true });
        if (!ok(deleting))
            return deleting;
    }
}

