/**
 * @fileoverview Add a description for your module here.
 */

export { invoke as default, describe };
import describeGraph from "@describe";

/**
 * The individual entry point that is being exposed in NodeDescriberResult
 */
export type NodeDescriberEntry = {
  title?: string;
  description: string;
  inputSchema: Schema;
};

export type NodeDescriberEntries = Record<string, NodeDescriberEntry>;

async function invoke({ context }: { context: LLMContent[] }) {
  console.log(await describeGraph({ url: "./tools.bgl.json" }));
  return { context };
}

async function describe() {
  return {
    entries: {
      weather: {
        title: "Get Weather",
        description: "Reports current weather",
        inputSchema: {
          type: "object",
          properties: {
            location: {
              type: "string",
              description: "Location",
            },
          },
        },
      },
      search: {
        title: "Search Web",
        description: "Searches the Web",
        inputSchema: {
          type: "object",
          properties: {
            query: {
              type: "string",
              description: "Search query",
            },
          },
        },
      },
    } satisfies NodeDescriberEntries,
    inputSchema: {
      type: "object",
      properties: {
        context: {
          type: "array",
          items: { type: "object", behavior: ["llm-content"] },
          title: "Context in",
        },
      },
    } satisfies Schema,
    outputSchema: {
      type: "object",
      properties: {
        context: {
          type: "array",
          items: { type: "object", behavior: ["llm-content"] },
          title: "Context out",
        },
      },
    } satisfies Schema,
  };
}
