/**
 * @fileoverview Generates music output using supplied context.
 */
import { ArgumentNameGenerator } from "./a2/introducer";
import { ListExpander } from "./a2/lists";
import { executeStep2, } from "./a2/step-executor";
import { Template } from "./a2/template";
import { ToolManager } from "./a2/tool-manager";
import { defaultLLMContent, encodeBase64, joinContent, ok, toLLMContent, toLLMContentInline, toLLMContentStored, toText, toTextConcat, } from "./a2/utils";
export { invoke as default, describe };
async function callMusicGen(prompt) {
    const executionInputs = {};
    executionInputs["prompt"] = {
        chunks: [
            {
                mimetype: "text/plain",
                data: encodeBase64(prompt),
            },
        ],
    };
    const inputParameters = ["prompt"];
    const body = {
        planStep: {
            stepName: "GenerateMusic",
            modelApi: "generate_music",
            inputParameters: inputParameters,
            systemPrompt: "",
            output: "generated_music",
        },
        execution_inputs: executionInputs,
    };
    const response = await executeStep2(body);
    if (!ok(response))
        return response;
    const { mimeType, data } = response.chunks.at(0);
    if (mimeType.endsWith("/storedData")) {
        return toLLMContentStored(mimeType.replace("/storedData", ""), data);
    }
    else {
        return toLLMContentInline(mimeType, data);
    }
}
async function invoke({ context, text, ...params }) {
    context ?? (context = []);
    let instructionText = "";
    if (text) {
        instructionText = toText(text).trim();
    }
    const template = new Template(toLLMContent(instructionText));
    const toolManager = new ToolManager(new ArgumentNameGenerator());
    const substituting = await template.substitute(params, async ({ path: url, instance }) => toolManager.addTool(url, instance));
    if (!ok(substituting)) {
        return substituting;
    }
    console.log("context");
    console.log(context);
    console.log("instruction");
    console.log(text);
    console.log("substituting");
    console.log(substituting);
    const results = await new ListExpander(substituting, context).map(async (itemInstruction, itemContext) => {
        const combinedInstruction = toTextConcat(joinContent(toText(itemInstruction), itemContext, false));
        if (!combinedInstruction) {
            return toLLMContent("Please provide the music prompt.");
        }
        console.log("PROMPT: ", combinedInstruction);
        return callMusicGen(combinedInstruction);
    });
    if (!ok(results))
        return results;
    return { context: results };
}
async function describe({ inputs: { text } }) {
    const template = new Template(text);
    return {
        inputSchema: {
            type: "object",
            properties: {
                context: {
                    type: "array",
                    items: { type: "object", behavior: ["llm-content"] },
                    title: "Context in",
                    behavior: ["main-port"],
                },
                text: {
                    type: "object",
                    behavior: ["llm-content", "config", "hint-preview"],
                    title: "Text",
                    description: "Construct the music generation prompt. Use @ to reference previous step outputs.",
                    default: defaultLLMContent(),
                },
                ...template.schemas(),
            },
            behavior: ["at-wireable"],
            ...template.requireds(),
        },
        outputSchema: {
            type: "object",
            properties: {
                context: {
                    type: "array",
                    items: { type: "object", behavior: ["llm-content"] },
                    title: "Context out",
                    behavior: ["hint-audio", "main-port"],
                },
            },
            additionalProperties: false,
        },
        title: "Make Music",
        metadata: {
            icon: "generative-audio",
            tags: ["quick-access", "generative"],
            order: 3,
        },
    };
}
