/**
 * @fileoverview Saves outputs using a provided connector.
 */
import { Template } from "./a2/template";
import { ok } from "./a2/utils";
export { invoke as default, describe };
async function invoke({ context, connectors, ...options }, caps) {
    const template = new Template(caps, connectors);
    const saving = await template.save(context, options);
    if (!ok(saving))
        return saving;
    return { context };
}
async function describe({ inputs }, caps) {
    const template = new Template(caps, inputs.connectors);
    return {
        title: "Save Outputs",
        description: "Saves outputs using a provided connector",
        metadata: {
            icon: "combine-outputs",
            tags: ["quick-access", "core", "experimental"],
            order: 102,
        },
        inputSchema: {
            type: "object",
            properties: {
                context: {
                    type: "array",
                    items: { type: "object", behavior: ["llm-content"] },
                    title: "Context in",
                    behavior: ["main-port"],
                },
                connectors: {
                    type: "object",
                    behavior: ["llm-content", "config", "hint-preview"],
                    title: "Connectors",
                    description: "Specify destination connectors: the outputs will be saved into these connectors",
                },
                ...(await template.schemaProperties()),
            },
            behavior: ["at-wireable"],
        },
        outputSchema: {
            type: "object",
            properties: {
                context: {
                    type: "array",
                    items: { type: "object", behavior: ["llm-content"] },
                    title: "Context out",
                    behavior: ["main-port"],
                },
            },
        },
    };
}
