const fn_4 = ({ merge, ...inputs }) => {
  const entries = Object.entries(inputs).sort();
  const context = [];
  if (merge) {
    const parts = [];
    for (const entry of entries) {
      const input = entry[1];
      const c = Array.isArray(input) ? input : [input];
      const last = c[c.length - 1];
      if (last) {
        if (last.role === "$metadata") {
          continue;
        }
        parts.push(...last.parts);
      }
    }
    context.push({ parts });
  } else {
    for (const entry of entries) {
      const input = entry[1];
      const c = Array.isArray(input) ? input : [input];
      context.push(...c);
    }
  }
  return { context };
};
