const fn_7 = ({ context: context2, progress }) => {
  const plans = Array.isArray(progress) ? progress : [progress];
  const existing = Array.isArray(context2) ? context2 : [context2];
  if (!plans || !plans.length) {
    throw new Error("Plan is required for Looper to function.");
  }
  try {
    const current = plans[0];
    if (current.done) {
      return { done: existing };
    }
    const originalPlan = plans[plans.length - 1];
    let max = originalPlan.max;
    const doneMarker = originalPlan.doneMarker;
    if (!max) {
      const planItems = originalPlan.todo?.length;
      if (planItems) {
        max = planItems + 1;
      } else {
        max = Infinity;
      }
    }
    const contents = structuredClone(existing);
    const count = plans.length;
    if (count >= max) {
      return { done: existing };
    }
    if (current.todo && Array.isArray(current.todo)) {
      const next = current.todo?.shift();
      if (!next) {
        return { done: existing };
      }
      delete current.doneMarker;
      contents.push({
        role: "$metadata",
        data: { ...current, next: next.task }
      });
      return { context: contents };
    } else if (doneMarker) {
      contents.push({
        role: "$metadata",
        data: { type: "looper", doneMarker }
      });
      return { context: contents };
    } else if (max) {
      const count2 = plans.length;
      if (count2 >= max) {
        return { done: existing };
      }
      contents.push({ role: "$metadata", data: { type: "looper", max } });
      return { context: contents };
    }
    return { done: existing };
  } catch (e) {
    const error = e;
    throw new Error(`Invalid plan, unable to proceed: ${error.message}`);
  }
};

const fn_3 = ({ context: context2, forkOutputs }) => {
  const fork = forkOutputs;
  const existing = Array.isArray(context2) ? context2 : [context2];
  const progress = [];
  for (let i = existing.length - 1; i >= 0; i--) {
    const item = existing[i];
    if (item.role === "$metadata") {
      progress.push(item.data);
    }
  }
  if (fork) {
    if (progress.length) {
      return { progress };
    } else {
      return { context: context2 };
    }
  } else {
    return { context: context2, progress };
  }
};

const fn_8 = ({ context: context2 }) => {
  if (!context2) throw new Error("Context is required");
  const c = context2;
  const result = c.filter((item) => item.role !== "$metadata");
  return { context: result };
};

const fn_4 = ({ context: context2, toAdd }) => {
  if (!context2) throw new Error("Context is required");
  const existing = Array.isArray(context2) ? context2 : [context2];
  const incoming = toAdd;
  if (!incoming.parts) {
    const containsUserRole = existing.filter(
      (item) => item.role !== "model" && item.role !== "$metadata"
    ).length > 0;
    if (!containsUserRole) {
      return {
        context: [
          ...existing,
          { role: "user", parts: [{ text: "Do your magic" }] }
        ]
      };
    }
    return { context: existing };
  }
  if (!incoming.role) {
    incoming.role = "user";
  }
  const last = existing[existing.length - 1];
  if (!last) {
    return { context: [incoming] };
  }
  if (last.role !== incoming.role) {
    return { context: [...existing, incoming] };
  } else {
    const result = structuredClone(existing);
    const index = result.length - 1;
    result[index].parts.push(...incoming.parts);
    return { context: result };
  }
};

