const fn_7 = ({ context, progress }) => {
  const contents = Array.isArray(context) ? context : [context];
  const plans = Array.isArray(progress) ? progress : [progress];
  const last = plans[0];
  if (!last || !last.next) {
    return { context };
  }
  const lastLooperMarker = contents.findLastIndex(
    (item) => item.role === "$metadata" && item.type === "looper"
  );
  if (lastLooperMarker >= 0) {
    const pastLooper = contents.slice(lastLooperMarker);
    const hasModel = pastLooper.some((item) => item.role === "model");
    if (hasModel) {
      return { context: contents };
    }
  }
  contents.push({ role: "user", parts: [{ text: last.next }] });
  return { context: contents };
};

const router = ({ context }) => {
  if (!context) throw new Error("Context is a required input");
  const item = context;
  const functionCallParts = item.parts
    .filter((part) => "functionCall" in part)
    .map((part) => part.functionCall);
  if (functionCallParts.length === 0) {
    const textPart = item.parts.find((part) => "text" in part);
    if (!textPart) throw new Error("No text or function call found in context");
    return { context, text: textPart.text };
  }
  return { context, functionCalls: functionCallParts };
};

const fn_5 = ({ context, toadd: toAdd }) => {
  if (!context) throw new Error("Context is required");
  const existing = Array.isArray(context) ? context : [context];
  const incoming = toAdd;
  if (!incoming.parts) {
    const containsUserRole =
      existing.filter(
        (item) => item.role !== "model" && item.role !== "$metadata"
      ).length > 0;
    if (!containsUserRole) {
      return {
        context: [
          ...existing,
          { role: "user", parts: [{ text: "Do your magic" }] },
        ],
      };
    }
    return { context: existing };
  }
  if (!incoming.role) {
    incoming.role = "user";
  }
  const last = existing[existing.length - 1];
  if (!last) {
    return { context: [incoming] };
  }
  if (last.role !== incoming.role) {
    return { context: [...existing, incoming] };
  } else {
    const result = structuredClone(existing);
    const index = result.length - 1;
