  const result = Object.fromEntries(
    r.map((item, i) => [`context-${i + 1}`, item.item])
  );
  if (c) {
    result["context-0"] = c;
  }
  return result;
};

const formatFunctionDeclarations = ({ list }) => {
  const tools = [];
  const urlMap = {};
  list.forEach((item) => {
    tools.push(item.function);
    const flags = item.flags;
    urlMap[item.function.name] = { url: item.boardURL, flags };
  });
  return { tools, urlMap };
};

