examples:
[
  {
    "prompt": "Given the following specs, extract requirements for writing an ad copy:\n    \n    This ad is for my lawn care company that will fit into an inch of newspaper copy. It's called \"Max's Lawn Care\" and it should use the slogan \"I care about your lawn.\" Emphasize the folksiness of it being a local, sole proprietorship that I started after graduating from high school.",
    "schema": {
      "type": "object",
      "properties": {
        "requirements": {
          "type": "array",
          "items": {
            "type": "string",
            "description": "an ad requirement"
          }
        }
      }
    }
  },
  {
    "prompt": "Write ad copy that conforms to the requirements above",
    "schema": {
      "type": "object",
      "properties": {
        "ad": {
          "type": "string",
          "description": "the ad copy"
        }
      }
    }
  }
]

