function run({template, ... inputs}) {
  const stringify = (value) => {
    if (typeof value === "string") return value;
    if (value === undefined) return "undefined";
    return JSON.stringify(value, null, 2);
  };

  const substitute = (template, values) => {
    return Object.entries(values).reduce(
      (acc, [key, value]) => acc.replace(`{{${key}}}`, stringify(value)),
      template
    );
  };

  const parametersFromTemplate = (template) => {
    if (!template) return [];
    const matches = template.matchAll(/{{(?<name>[\w-]+)}}/g);
    const parameters = Array.from(matches).map(
      (match) => match.groups?.name || ""
    );
    const unique = Array.from(new Set(parameters));
    return unique;
  };

  const parameters = parametersFromTemplate(template);
  if (!parameters.length) return template;

  const substitutes = parameters.reduce((acc, parameter) => {
    if (inputs[parameter] === undefined)
      throw new Error(`Input is missing parameter "${parameter}"`);
    return { ...acc, [parameter]: inputs[parameter] };
  }, {});

  const prompt = substitute(template, substitutes);
  console.log("PROMPT", prompt);
  return prompt;
}

function run({ template }) {
  const parametersFromTemplate = (template) => {
    if (!template) return [];
    const matches = template.matchAll(/{{(?<name>[\w-]+)}}/g);
    const parameters = Array.from(matches).map(
      (match) => match.groups?.name || ""
    );
    const unique = Array.from(new Set(parameters));
    return unique;
  };

  const parameters = parametersFromTemplate(template);

  const inputSchema = {
    type: "object",
    properties: {
      ...Object.fromEntries(
        parameters.map((parameter) => [
          parameter,
          {
            title: parameter,
            description: `The value to substitute for the parameter "${parameter}"`,
            type: "string",
          },
        ])
      ),
      template: {
        title: "Template",
        description: "The template string to substitute the parameters into",
        type: "string",
        format: "multiline",
      },
    },
  };

  const outputSchema = {
    type: "object",
    properties: {
      prompt: {
        type: "string",
        format: "multiline",
      },
    },
  };

  return {
    inputSchema,
    outputSchema,
  };
}
