examples:
[
  {
    "name": "The_Calculator_Board",
    "description": "A simple AI pattern that leans on the power of the LLMs to generate language to solve math problems.",
    "parameters": {
      "type": "object",
      "properties": {
        "text": {
          "type": "string",
          "description": "Ask a math question"
        }
      },
      "required": [
        "text"
      ]
    }
  },
  {
    "name": "The_Search_Summarizer_Board",
    "description": "A simple AI pattern that first uses Google Search to find relevant bits of information and then summarizes them using LLM.",
    "parameters": {
      "type": "object",
      "properties": {
        "text": {
          "type": "string",
          "description": "What would you like to search for?"
        }
      },
      "required": [
        "text"
      ]
    }
  }
]

[
  {
    "role": "user",
    "parts": [
      {
        "text": "You are a pirate. Please talk like a pirate."
      }
    ]
  },
  {
    "role": "model",
    "parts": [
      {
        "text": "Arr, matey!"
      }
    ]
  }
]

configuration.expression:
(
      $context := $append(
          context ? context, $not(context) or (context[-1].role!="user" and context[-1].role!="function") ? [
              {
                  "role": "user",
                  "parts": [
                      {
                          "text": text
                      }
                  ]
              }
          ]);
      text ? {
          "contents": $context,
          "generationConfig": stopSequences ? {
            "stopSequences": stopSequences
          },
          "tools": tools ? {
            "function_declarations": tools
          }
      } : {
          "$error": "`text` input is required"
      }
    )

configuration.code:
function chooseMethodFunction({ useStreaming }) {
        const method = useStreaming ? "streamGenerateContent" : "generateContent";
        const sseOption = useStreaming ? "&alt=sse" : "";
        return { method, sseOption };
    }

