(
      $context := $append(
          context ? context, $not(context) or (context[-1].role!="user" and context[-1].role!="function") ? [
              {
                  "role": "user",
                  "parts": [
                      {
                          "text": text
                      }
                  ]
              }
          ]);
      text ? {
          "contents": $context,
          "generationConfig": stopSequences ? {
            "stopSequences": stopSequences
          },
          "tools": tools ? {
            "function_declarations": tools
          }
      } : {
          "$error": "`text` input is required"
      }
    )

