import { connect, exportFile, unwrap } from "./api";

export { invoke as default, describe };

async function invoke({ id }) {
  if (!id) {
    return error("Please provide Google Drive File ID");
  }
  const token = await connect({
    title: "Get API Token",
    description: "Requesting Google Drive API Auth token",
  });
  const text = unwrap(
    await exportFile(token, id, "text/markdown", {
      title: "Export to Markdown",
      description: "Exporting specified Google Drive file to Markdown",
    }),
    `Unable to export file with id "${id}" to Markdown`
  );
  return { context: [{ parts: [{ text }], role: "user" }] };
}

async function describe() {
  return {
    inputSchema: {
      type: "object",
      properties: {
        id: {
          type: "string",
          title: "File ID",
          behavior: ["google-drive-file-id"],
        },
      },
    },
    outputSchema: {
      type: "object",
      properties: {
        context: {
          type: "array",
          items: { type: "object", behavior: ["llm-content"] },
          title: "Context out",
        },
      },
    },
  };
}

function error($error) {
  return { $error };
}

