/**
 * @fileoverview Add a description for your module here.
 */

import invokeGraph from "@invoke";
import output from "@output";

import { EVAL_SET } from "./eval-set";

export { invoke as default, describe };

export type Inputs = {
  context: LLMContent[];
};

export type Outputs = {
  context: LLMContent[];
};

async function autoname(json: JsonSerializable, description: string) {
  const inputs: Inputs = {
    context: [{ parts: [{ json }] }],
  };
  const autonaming = (await invokeGraph({
    $board: "file://sideboards-bgl/autoname.bgl.json",
    ...inputs,
  })) as Outcome<Outputs>;
  if ("$error" in autonaming) {
    console.error("ERROR AUTONAMING", autonaming.$error);
    return;
  }
  await report(autonaming.context, description);
}

async function invoke(): Promise<Outcome<Outputs>> {
  for (const item of EVAL_SET) {
    const { description, ...inputs } = item;
    await autoname(inputs, description);
  }
  return { context: [] };
}

async function describe() {
  return {
    inputSchema: {
      type: "object",
      properties: {},
    } satisfies Schema,
    outputSchema: {
      type: "object",
      properties: {
        context: {
          type: "array",
          items: { type: "object", behavior: ["llm-content"] },
          title: "Context out",
        },
      },
    } satisfies Schema,
  };
}

async function report(
  details: LLMContent[],
  description: string
): Promise<boolean> {
  const title = description;

  const detailsSchema: Schema = {
    title,
    type: "object",
    behavior: ["llm-content"],
  };

  const schema: Schema = {
    type: "object",
    properties: {
      details: detailsSchema,
    },
  };

  const { delivered } = await output({
    $metadata: {
      title,
    },
    schema,
    details,
  });
  return delivered;
}

