/**
 * @fileoverview Handles node configuration updates
 */

import { isLLMContent, toText, llm } from "./a2/utils";
import { type GeminiSchema } from "./a2/gemini";
import type { AutonameMode, Arguments } from "./types";

export { NodeConfigurationUpdateMode };

const USER_INPUT_TYPE =
  "embed://a2/a2.bgl.json#21ee02e7-83fa-49d0-964c-0cab10eafc2c";
const GENERATE_TYPE = "embed://a2/generate.bgl.json#module:main";
const DISPLAY_TYPE = "embed://a2/a2.bgl.json#module:render-outputs";

type StepHandler = {
  canAutoname: boolean;
  prompt: LLMContent[];
};

type StepMap = Map<
  string,
  (configuration: Record<string, JsonSerializable>) => StepHandler
>;

const STEPS: StepMap = new Map([
  [
    USER_INPUT_TYPE,
    (c) =>
      createStepHandler(
        "to ask the application user for input",
        textFromConfiguration(c, ["description"])
      ),
  ],
  [
    GENERATE_TYPE,
    (c) =>
      createStepHandler(
        "as a prompt for an LLM",
        textFromConfiguration(c, ["config$prompt"])
      ),
  ],
  [
    DISPLAY_TYPE,
    (c) =>
      createStepHandler(
        "as a prompt for an LLM to render HTML for display",
        textFromConfiguration(c, ["text"])
      ),
  ],
]);

function createStepHandler(
  typeSpecficPrompt: string,
  text: string
): StepHandler {
  console.log("TEXT", text);
  const words = text.split(/\s+/);
  return {
    canAutoname: words.length > 5,
    prompt: [
      llm`
  Analyze the text below and provide suggestions for title and description that could be used
  to automatically label this text as one of the steps in a visual, a no-code application builder.
  Builders are creating applications by placing steps on a canvas and wiring them together into a visual flow.
  This text will be used by one of the steps ${typeSpecficPrompt}.

  Important:
  - Both the title and intent must be accurate, concise, and specific to the text
  - The description must be one sentence
  - Each title must be verb-first, action oriented, short and to the point
  - The builders are non-technical, so avoid overly technical jargon

  == begin text ==
  ${text}
  == end text ==
  `.asContent(),
    ],
  };
}

const DEFAULT_STEP_HANDLER: StepHandler = {
  canAutoname: false,
  prompt: [],
};

function stepHandlerFromArgs(args: Arguments): StepHandler {
  const type = args.nodeConfigurationUpdate?.type;
  const configuration = args.nodeConfigurationUpdate?.configuration;
  if (!type || !configuration) return DEFAULT_STEP_HANDLER;
  const factory = STEPS.get(type);
  if (!factory) return DEFAULT_STEP_HANDLER;
  return factory(configuration);
}

class NodeConfigurationUpdateMode implements AutonameMode {
  #stepHandler: StepHandler;

  constructor(public readonly args: Arguments) {
    this.#stepHandler = stepHandlerFromArgs(args);
  }

  canAutoname() {
    return this.#stepHandler.canAutoname;
  }
  prompt(): LLMContent[] {
    return this.#stepHandler.prompt;
  }

  schema(): GeminiSchema {
    return {
      type: "object",
      properties: {
        title: {
          type: "string",
          description:
            "Suggested title for the prompt, verb-first, action oriented. Two words.",
        },
        description: {
          type: "string",
          description:
            "Suggested description for the prompt. Seven words or less",
        },
      },
      required: ["title", "description"],
    };
  }
}

function textFromConfiguration(
  configuration: Record<string, JsonSerializable> | undefined,
  allow: string[]
): string {
  if (!configuration) return "";

  return Object.entries(configuration)
    .map(([name, value]) => {
      if (!allow.includes(name)) return "";
      if (isLLMContent(value)) {
        return toText(value);
      }
      return JSON.stringify(value);
    })
    .join("");
}

/**
 * @fileoverview Common types.
 */

import type { GeminiSchema } from "./a2/gemini";

export type NodeConfigurationUpdate = {
  configuration?: Record<string, JsonSerializable>;
  /**
   * Node type
   */
  type?: string;
};

export type Arguments = {
  nodeConfigurationUpdate?: NodeConfigurationUpdate;
};

export type AutonameMode = {
  canAutoname(): boolean;
  prompt(): LLMContent[];
  schema(): GeminiSchema;
};

/**
 * Represents an edge in a graph.
 */
export type Edge = {
  /**
   * The node that the edge is coming from.
   */
  from: string;

  /**
   * The node that the edge is going to.
   */
  to: string;

  /**
   * The input of the `to` node. If this value is undefined, then
   * the then no data is passed as output of the `from` node.
   */
  in?: string;

  /**
   * The output of the `from` node. If this value is "*", then all outputs
   * of the `from` node are passed to the `to` node. If this value is undefined,
   * then no data is passed to any inputs of the `to` node.
   */
  out?: string;

  /**
   * If true, this edge is optional: the data that passes through it is not
   * considered a required input to the node.
   */
  optional?: boolean;

  /**
   * If true, this edge acts as a constant: the data that passes through it
   * remains available even after the node has consumed it.
   */
  constant?: boolean;
};

type PartialGraphDescriptor = {
  title?: string;
  metadata?: {
    intent?: string;
    instruction?: string;
    userModified?: string;
    visual?: unknown;
  };
  parameters?: {
    type: "object";
    properties: {
      context: {
        type: "string";
      };
    };
  };
  description?: string;
  nodes: PartialNodeDescriptor[];
  edges: Edge[];
};

type PartialNodeDescriptor = {
  id: string;
  configuration: Record<string, object>;
  metadata?: {
    title?: string;
    description?: string;
    userModified?: boolean;
  };
};
